
public class CharDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char c1 = 88; //ASCII value and Unicode value for X
		char c2 = 'X'; //char must be enclosed in single quotes
		char c3 = '\u0950';
		System.out.println("Value of char c1 and c2 and c3 are ");
		System.out.println(c1+" "+c2);
		
		System.out.println(c2++);
		System.out.println(c2);
		System.out.println(++c2);
		
		
		
		
		
		
		
		

	}

}
