
public class Lightspeed {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int days = 1000;
		long seconds = days * 24 * 60 * 60;
		
		long distance;
		
		//speed of light in miles per sec = 186000
		
		distance = seconds * 1860000;
		
		System.out.println("light will travel "+distance+" miles in 1000 days");
		

	}

}
